copySink.preCopyScript:
BEGIN TRY
IF NOT EXISTS (SELECT * FROM sys.schemas WHERE name = 'dbo') EXEC('Create Schema [dbo]');
END TRY
BEGIN CATCH
if ERROR_NUMBER() <> 2714 throw;
END CATCH

IF OBJECT_ID('[dbo].[Occupation]', 'U') IS NULL CREATE TABLE [dbo].[Occupation] ([OccupationId] int,
[OccupationName] varchar(60));

